package com.ucontrol.dapweb.controller;import com.ucontrol.dapweb.entity.Point;import com.ucontrol.dapweb.service.InfluxService;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;import java.util.ArrayList;@RestController@RequestMapping(value = "/api")public class InfluxdbApi {    @Resource    private InfluxService influxSvr;    @RequestMapping(value = "/influxdb/ping")    public boolean pingInfluxDB(){        return influxSvr.ping();    }    @RequestMapping(value = "/influxdb/get_point_by_name/{name}", method = RequestMethod.GET)    public ArrayList<Point> getPointByName(@PathVariable("name") String name){        return influxSvr.getPointByName(name);    }}