package com.ucontrol.dapweb.entity.appmodels;import java.util.ArrayList;public class Device {    public Device(int index, String name) {        this.index = index;        this.name = name;    }    private int id;    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    private int index;    private String name;    private ArrayList<Item> itemList;    public int getIndex() {        return index;    }    public void setIndex(int index) {        this.index = index;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public ArrayList<Item> getItemList() {        return itemList;    }    public void setItemList(ArrayList<Item> itemList) {        this.itemList = itemList;    }}