package com.ucontrol.dapweb.entity.appmodels;import java.util.ArrayList;public class Unit {    public Unit(int index, String name){        this.index = index;        this.name = name;    }    private int id;    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    private int index;    private String name;    private ArrayList<PlantSystem> systemList;    public int getIndex() {        return index;    }    public void setIndex(int index) {        this.index = index;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public ArrayList<PlantSystem> getSystemList() {        return systemList;    }    public void setSystemList(ArrayList<PlantSystem> systemList) {        this.systemList = systemList;    }}